import React, { useState, useEffect } from 'react';
import FilterPanel from './FilterPanel';
import ProductCard from './ProductCard';
import Pagination from './Pagination';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 10;

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    fetch(`http://127.0.0.1:5000/api/products`)
      .then(response => response.json())
      .then(data => {
        setProducts(data);
        setFilteredProducts(data);
      })
      .catch(error => console.error('Error fetching products:', error));
  };

  const handleFilterChange = (filters) => {
    let filtered = products;

    if (filters.category) {
      filtered = filtered.filter(product => product.main_category === filters.category);
    }
    if (filters.minPrice !== undefined) {
      filtered = filtered.filter(product => product.discount_price >= filters.minPrice);
    }
    if (filters.maxPrice !== undefined) {
      filtered = filtered.filter(product => product.discount_price <= filters.maxPrice);
    }

    setFilteredProducts(filtered.slice((currentPage - 1) * productsPerPage, currentPage * productsPerPage));
  };

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
    setFilteredProducts(products.slice((pageNumber - 1) * productsPerPage, pageNumber * productsPerPage));
  };

  return (
    <div className="dashboard">
      <h1 className="dashboard-title">Amazon Search Dashboard</h1>
      <div className="filter-container">
        <FilterPanel onFilterChange={handleFilterChange} />
      </div>
      <div className="filters-and-products">
        <div className="product-list">
          {filteredProducts.map(product => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      </div>
      <Pagination
        productsPerPage={productsPerPage}
        totalProducts={filteredProducts.length}
        paginate={paginate}
        currentPage={currentPage}
      />
    </div>
  );
};

export default Dashboard;
